// JScript source code

var fs = require('fs');
// var path = 'C:\\MarketData\\20111010\\atsmsg.txt';
var path = '.\\JUNK.txt';
var   buffer = '';

var   stream = fs.createReadStream(path);
stream.on('end', function() {
    console.log('>> simulation data file end');
});

stream.on('data', function (data) {
    var i=-1;
    buffer += data; 
    i = buffer.indexOf('\n');
    while (i>-1) {
        var    line = buffer.substring(0, i); 
        console.log(line);
        buffer = buffer.substring(i+1);
        i = buffer.indexOf('\n');
    }
});

